package breaks.statement;

public class ContinueClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char chArr[] = {'r','a','e','C','r','z','k','x'};
		
		for (int i = 0; i<chArr.length;i++)
		{
			
			if (chArr[i]=='r')
			{				
				continue;
				
			}
			System.out.println(chArr[i]);
			
		}

	}

}
